// FlowMind AI Sales Process Analysis
const leads = $input.first().json;
const opportunities = $input.last().json;

// Analyze lead-to-opportunity conversion patterns
const conversionAnalysis = {
  totalLeads: leads.length,
  totalOpportunities: opportunities.length,
  conversionRate: (opportunities.length / leads.length) * 100,
  avgDealSize: opportunities.reduce((sum, opp) => sum + (opp.Amount || 0), 0) / opportunities.length,
  pipelineHealth: 'Good'
};

// Identify process bottlenecks
const stageAnalysis = {};
opportunities.forEach(opp => {
  const stage = opp.StageName;
  if (!stageAnalysis[stage]) {
    stageAnalysis[stage] = { count: 0, totalValue: 0, avgDays: 0 };
  }
  stageAnalysis[stage].count++;
  stageAnalysis[stage].totalValue += opp.Amount || 0;
});

// Generate AI insights
const insights = {
  processEfficiency: conversionAnalysis.conversionRate > 20 ? 'High' : 'Needs Improvement',
  recommendations: [
    conversionAnalysis.conversionRate < 15 ? 'Improve lead qualification process' : null,
    'Automate follow-up sequences for better conversion',
    'Implement lead scoring for priority handling'
  ].filter(Boolean),
  bottlenecks: Object.entries(stageAnalysis)
    .filter(([stage, data]) => data.count > opportunities.length * 0.3)
    .map(([stage]) => `High volume in ${stage} stage`),
  predictedRevenue: Object.values(stageAnalysis).reduce((sum, data) => sum + data.totalValue, 0)
};

return [{
  json: {
    analysis: conversionAnalysis,
    stageBreakdown: stageAnalysis,
    aiInsights: insights,
    timestamp: new Date().toISOString(),
    source: 'salesforce_pipeline'
  }
}];

